insert into fanfics (name, fandom, genre, rating, status, "desc", link) values
    ('Превратить в серебро и успех', '"Harry Potter"', 'Джен', 'R', 'В процессе', E'Попаданка в чистокровную волшебницу (не из списка священных двадцати восьми), времена Тома Реддла. Слизерин головного мозга, жажда денег и возможностей, знание канона и отсутствие желания наносить добро и причинять справедливость незнакомым людям прилагаются.', 'https://ficbook.net/readfic/9396691'),
    ('Whale 52', '"Boku no Hero "Academia"', 'Джен', 'R', 'В процессе', E'Японские иероглифы воспринимались с трудом, хоть я их и знала. Я перечитала ещё раз... Сугавара Лейко. Сугавара Лейко — моё имя, получается? Я посмотрела на свои маленькие ручки и поджала пальцы на босых ногах. Получается, имя.\n[ОЖП-попаданка в девочку со способностью, за которую приходится расплачиваться проблемами с речью и письмом]', 'https://ficbook.net/readfic/7921394'),
    ('Афелий', '"Boruto: Naruto Next Generations"', 'Джен', 'R', 'В процессе', E'Я проснулась один раз, второй, третий... На шестой воспоминания о прошлой жизни и предыдущих пяти попытках меня всё-таки догнали. Ощущения были пренеприятнейшие. Ну, Орочимару, «родитель» нашелся, кто ж на своём ребёнке такие эксперименты ставит?!\n[ахтунг! события из «Боруто» станут основой сюжета, но они будут щедро переписываться и переосмысливаться]', 'https://ficbook.net/readfic/9339485'),
    ('Птичий крик', '"Naruto", "The Gamer"', 'Джен', 'R', 'В процессе', E'Имя: Тори Каору.\nСтатус: Надежда родителей.\nУровень: 3.\nВход в систему, пожалуйста, подождите...\n[ОЖП-попаданка в бесклановую куноичи]', 'https://ficbook.net/readfic/5583951'),
    ('Худший друг', '"Genshin Impact"',  'Слэш', 'NC-17', 'Завершен', E'Корабль Бэй Доу был большим. Он вмещал в себя коробки с грузами, законными и контрабандными; он вмещал в себя людей, бегущих от себя и бегущих от закона (иногда — бегущих от всего).\nИ всё же ни разу до этого Казуха не видел подобного пассажира.\n— Ты пахнешь грозой и штормом. А ещё пеплом и одиночеством. Стоялый запах, древний.\n— Ты, — хмурый парень со смутно знакомым лицом поднял на Казуху взгляд и неприятно прищурился, — хочешь сказать, что я воняю?', ''),
    ('Богатство не уменьшает жадности', '"Genshin Impact"', 'Слэш', 'NC-17', 'Завершен', '— Ты принадлежишь клану Камисато, Тома. Ты пообещал защищать всё, что принадлежит мне, и это включает тебя. Не позволяй себя ранить.', 'https://ficbook.net/readfic/11011638'),
    ('Молчи и лечи', '"Genshin Impact"', 'Гет', 'PG-13', 'Завершен', E'В первый раз они схлестнулись всерьёз: один нападал на Ли Юэ, другая его защищала. Второй раз возник из желания реванша Предвестника. Третий... третий и все, что далее, они уже не считали.\nЛюмин резким жестом размотала бинт и привычно уже начала обматывать им чужую руку. После битвы всегда следовало зализать раны, и вместе это делалось сподручнее.\nНо Тарталья постоянно болтал. Она разбивала ему в кровь губы — он всё равно продолжал болтать.', 'https://ficbook.net/readfic/10517535'),
    ('Лучше', '"Genshin Impact"', 'Слэш', 'G', 'Завершен', E'На самом деле ей Альбедо даже жаль. Фигура Путешественника, сошедшая с холста, никогда не заменит самого Путешественника и никогда не сможет ответить. Альбедо мог бесконечно прикрываться своими экспериментами, но Розария легко различала тоску по недостижимому в чужом взгляде. Она, чёрт возьми, служительница церкви.', 'https://ficbook.net/readfic/10515439'),
    ('Гордость', '"Naruto"', 'Джен', 'G', 'Завершен', E'Вчерашний Неджи думал, что гордость — это единственное, что у него осталось. Сегодняшний Неджи думает, что гордость — это то, с чего у него всё начнётся.', 'https://ficbook.net/readfic/8353364'),
    ('Treasure', '"Kuroko no Basuke"', 'Слэш', 'R', 'Завершен', E'Шанс на вторую жизнь — это ли не здорово? Но, возможно, этот шанс будет не таким лёгким и светлым, как могло бы показаться. Ведь в прошлой жизни ты: даже не успел её закончить; японский знал откровенно хреново; не был отличником; еле как пережил подростковый возраст, не убившись в процессе.\nДобро пожаловать в баскетбольный клуб школы Ракузан!', 'https://ficbook.net/readfic/6826570'),
    ('Из клетки', '"Dragon Age"', 'Слэш', 'R', 'Завершен', E'Уже собирающийся свалить из Башни Бдения на поиски своего любовника Амелл очень негативно отреагировал на мысль воссоединения с духом, а потому взял Андерса за шкирку, закинул на корабль и наказал уехать подальше от Справедливости... то есть, посмотреть, что в чертовом Городе Цепей происходит.', 'https://ficbook.net/readfic/6078006'),
    ('В неизвестность', '"Dragon Age"', 'Слэш', 'R', 'Завершен', E'Андерс никогда не был хорошим травником, так что и «улучшенное» лириумное зелье у него получилось... не самого лучшего качества. Никакой дополнительной маны оно не давало, но память очищало просто волшебно. Так Амелл и проснулся в день своих Истязаний, не помнящий совершенно ничего.', 'https://ficbook.net/readfic/5213378'),
    ('Драконья броня', '"Harry Potter"', 'Джен', 'PG-13', 'Завершен', E'Вместо рук почему-то были крылья, а на мир приходилось смотреть сквозь прутья клетки. Я сворачивалась клубочком и впадала в депрессию первые пару дней. Потом надоело.\nХвосторога, значит. Тонны лишнего веса, значит. Неизвестное количество детей в — я покосилась на кладку — обличии яиц, значит.\nНичего, прорвусь как-нибудь.', 'https://ficbook.net/readfic/4743114'),
    ('Неудачники', '"Naruto"', 'Слэш', 'NC-17', 'Завершен', E'Прежде чем на долгие годы распрощаться с Конохой, Саске увидел ничем не примечательного ребёнка. Глаза чернявые, кожа белая, только лицо — как у старшего брата, но ещё более женственное, по-девичьи пока наивное. Увидел — и отшатнулся.\nА девочка посмотрела недоумевающе, улыбнулась щербатым от выпадающих молочных зубов ртом и спросила:\n— А вы кто, дяденька?', 'https://ficbook.net/readfic/3774501'),
    ('Жги огонь, поджидай гостей', '"Naruto"', 'Гет', 'R', 'Завершен', E'Я сидела перед обломком зеркала и до крови сжимала его края, вглядываясь в отражение. Стервозное личико, большие карие глаза и очень приметный длинный локон бордово-красных волос, склоненный в правую сторону. Одна из подчинённых Орочимару, владелица Джуин и просто душка, что украла Саске из деревни. Замечательно. Нет, просто отлично! И что мне с тобой делать, Хокумон но Таюя?', 'https://ficbook.net/readfic/3046644'),
    ('Чара', '"Undertale"', 'Джен', 'G', 'Завершен', E'Чара — это и есть вы, игрок. Вы следите за приключениями упавшего человека, будучи всего лишь бесплотным духом, который, однако, может влиять на ход событий через общение с Фриск.\n\nP.S. А иногда — и вовсе взять управление на себя.', 'https://ficbook.net/readfic/4616280'),
    ('Down with the Fallen', '"Naruto"', 'Гет', 'G', 'Завершен', E'У Хаширамы Сенджу была насыщенная жизнь — и почему-то оказалось такое же насыщенное посмертие. А как иначе объяснить, что он всё ещё жив (относительно) и даже может общаться с одной прелестной маленькой особой по имени Хината Хьюга?', 'https://ficbook.net/readfic/5414790'),
    ('Точка невозврата', '"Naruto"', 'Гет', 'PG-13', 'Завершен', E'А что, если Шисуи с помощью Котоамацуками убедит Данзо, когда тот попытается отобрать у него глаза, что клан Учиха не опасен? Что, если восстание всё же будет отменено... или же произойдёт значительно позже?\nИстория клана Учиха от лица одного из его членов.', 'https://ficbook.net/readfic/5411033'),
    ('Изменить неизменное', '"Naruto"', 'Гет', 'PG-13', 'Завершен', E'Хьюга Хината — маленькая зажатая химе, которую за слабость презирает весь клан... Ну уж нет! Новая Хината ещё покажет всем, где раки зимуют!', 'https://ficbook.net/readfic/2098350'),
    ('В глазах костёр', '"Naruto"', 'Джен', 'PG-13', 'Завершен', E'Женщина приподнялась на руках, услышала шаги и вскинула голову, ловя полный ужаса взгляд подростка. У неё тут же перехватило дыхание: красная радужка со странным чёрным рисунком. В голове вспыхнуло имя. Итачи.\nПопаданка в Микото Учиха.', 'https://ficbook.net/readfic/3237075'),
    ('Зависимость', '"Сверхъестественное"', 'Слэш', 'PG-13', 'Завершен', E'2014!AU Сначала Кастиэль был зависим от своих крыльев. Следом пришли алкоголь и секс, ещё чуть позже — наркотики. А потом Кас вдруг понял, что его самая главная и болезненная зависимость всегда рядом. И зовут её Дин Винчестер.', 'https://ficbook.net/readfic/3774566'),
    ('Ястребиное зрение, человечьи глаза', '"Naruto"', 'Джен', 'G', 'Завершен', E'Когда Сараде Учиха исполнилось двенадцать, она узнала о легендарном змеином саннине, человеке, который победил саму смерть и по слухам владеет невероятными знаниями.', 'https://ficbook.net/readfic/3227521'),
    ('Хочешь — в рай, а хочешь — в ад', 'Ориджиналы', 'Джен', 'G', 'Завершен', E'— Я подожду еще года три-четыре, пока не наступит полная разруха и народ не взвоет. А потом возьму Меч Света, оседлаю нашего вороного, если он не помрет к тому времени, и поеду по стране, верша подвиги направо и налево. До победного конца. Потому что, — он позволил себе короткую злорадную улыбочку, — добро всегда побеждает... (с)', 'https://ficbook.net/readfic/3536703'),
    ('Впадая в детство', '"Harry Potter"', 'Джен', 'PG-13', 'Завершен', E'Когда Гарри Поттер появился на свет, звёзды в небе явно сошлись не в его пользу: он родился с редким заболеванием, гипофизарным нанизмом, расстройством гормона роста. Конечно же, магия сгладила острые углы этого недуга, сделав Гарри просто маленьким ребёнком, очень даже милым, но... Как вам Мальчик-Который-Выжил, ведущий себя словно дитя?', 'https://ficbook.net/readfic/2550873'),
    ('Солнце горит во мне', '"Naruto"', 'Джен', 'G', 'Завершен', E'Лёгкая придурковатость делает человека практически неуязвимым. (с)', 'https://ficbook.net/readfic/3142986'),
    ('Своё можно вернуть всегда', '"Лукьяненко Сергей «Дозоры»"', 'Слэш', 'PG-13', 'Завершен', E'— Своё можно вернуть всегда, — мрачно повторил Антон слова Завулона. Интересно, как Тёмный маг сможет вернуть ему Силу? Да и вообще, о способностях ли Иного он говорил?', 'https://ficbook.net/readfic/2931355'),
    ('Животное', 'Ориджиналы', 'Гет', 'G', 'Завершен', E'— Ты... ты животное! — задохнулась от переизбытка эмоций девушка. — Кто знает, что у тебя на уме!', 'https://ficbook.net/readfic/2918184'),
    ('Эй, жители неба!', 'Хантер Эрин «Коты-Воители»', 'Джен', 'G', 'Завершен', E'«— А это правда, что ты не любишь рыбу? — на одном дыхании, перебивая уже открывшую рот и издавшую несколько звуков Льдинку, выпалила зеленоглазая кошка. — Ага! Мышки вкуснее. Они такие… не такие мягкие, конечно, но сочные, да и без чешуи! Их ведь сразу можно есть, даже не очищая!» И что творится в Речном племени?', 'https://ficbook.net/readfic/2228442'),
    ('Русалка', '"Naruto"', 'Гет', 'PG-13', 'Завершен', E'Кисаме в очередной раз выглянул за борт корабля, потревоженный странным ощущением. Ему уже второй день казалось, что что-то зовёт, тянет в море... На этот раз он заметил в воде розоватый хвост.', 'https://ficbook.net/readfic/2485064'),
    ('Красноволосая богиня', '"Naruto"', 'Джен', 'G', 'Завершен', E'Нагато просто старалась сделать этот мир лучше. Она даже подумать не могла, что всё зайдёт так далеко и она станет главой преступной организации. Но Нагато всегда следовала тому пути, что они выбрали вместе. И пусть одного из них теперь нет, она дойдёт. Дойдёт до конца, чего бы это не стоило. Даже если она, всего-навсего, слабая запутавшаяся женщина.', 'https://ficbook.net/readfic/2403818'),
    ('Джинни', '"Harry Potter"', 'Гет', 'G', 'Завершен', E'Джинни никогда не любила своё полное имя.\n[AU, где у Гарри полное имя Гарольд, а у Джинни — Верджинья]', 'https://ficbook.net/readfic/2365160'),
    ('Внутри меня водица', '"Naruto"', 'Джен', 'G', 'Завершен', E'Да уж... нежданчик, конечно, огромный. Но ничего, прорвёмся, не даром я теперь водяной местного разлива! Ну и что, что стройный, молодой и вообще обаяшка — водяные тоже разные бывают. Правда вот, этот странного вида мужик с красивыми глазками начинает немного раздражать... странный он. Я даже до сих пор не уверен, что он мужик, если честно. Да, забыл представиться! Меня ведь теперь Хозуки Суйгецу зовут!', 'https://ficbook.net/readfic/2338933'),
    ('Мафия по-шинобийски', '"Naruto"', 'Джен', 'G', 'Завершен', E'Саске с непроницаемым лицом наблюдал за этим театром, по-другому не скажешь. Ну как, как можно было споткнуться и влететь в чужую дверь? Да ещё и утащить с собой Сакуру? Нет, он, Учиха, этого никак не понимал. Впрочем, как он уже заметил, Узумаки полон необъяснимых тайн и загадок.', 'https://ficbook.net/readfic/2101037');
